    public static int DetermineFloor(string input)
    {
        int floor = 0;
        foreach (char c in input)
        {
            if (c == '(')
            {
                floor++;   
            }
            else if (c == ')')
            {
                floor--;
            }
        }
        return floor;
    }
    
